import{_ as t}from"./image8.5ef9382c.js";import{_ as s,o as a,c as e,a as n}from"./app.045bf462.js";var p="/TIL/assets/1.76ee5dd1.png",r="/TIL/assets/2.7cb0254c.png",o="/TIL/assets/3.6653c6af.png",i="/TIL/assets/4.f3b707e6.png",u="/TIL/assets/5.fd50ea68.png";const c={},d=n('<h1 id="\u1107\u1167\u11AB\u1112\u116A\u1102\u1173\u11AB-\u1100\u1161\u1109\u1175\u1112\u116A\u1103\u116C\u11AB-\u1112\u1162\u11BC\u1103\u1169\u11BC\u110B\u1166\u1109\u1165-\u1109\u1175\u110C\u1161\u11A8\u1103\u116C\u11AB\u1103\u1161" tabindex="-1"><a class="header-anchor" href="#\u1107\u1167\u11AB\u1112\u116A\u1102\u1173\u11AB-\u1100\u1161\u1109\u1175\u1112\u116A\u1103\u116C\u11AB-\u1112\u1162\u11BC\u1103\u1169\u11BC\u110B\u1166\u1109\u1165-\u1109\u1175\u110C\u1161\u11A8\u1103\u116C\u11AB\u1103\u1161" aria-hidden="true">#</a> \uBCC0\uD654\uB294 \uAC00\uC2DC\uD654\uB41C \uD589\uB3D9\uC5D0\uC11C \uC2DC\uC791\uB41C\uB2E4</h1><p>\uC77C\uC694\uC77C \uC624\uC804 6\uC2DC, \uC774\uC720\uB294 \uBAA8\uB974\uACA0\uC9C0\uB9CC \uC790\uC5F0\uC2A4\uB7FD\uAC8C \uB208\uC774 \uB5A0\uC84C\uB2E4. <s>\uC0AC\uC2E4 \uC5B4\uC81C \uC800\uB141\uC5D0 \uB108\uBB34 \uB9E4\uC6B4\uAC78 \uBA39\uC5B4\uC11C \uADF8\uB7F0\uAC00.. \uBC30\uC544\uD30C\uC11C \uC77C\uC5B4\uB0AC\uB2E4.</s></p><p>\uBB34\uC5C7\uC744 \uD560\uAE4C \uACE0\uBBFC\uD558\uB2E4\uAC00 \uC120\uC120\uD574\uC9C4 \uB0A0\uC528\uAC00 \uC0DD\uAC01\uB098\uC11C 30\uBD84\uC815\uB3C4 \uC870\uAE45\uC744 \uD558\uBA74\uC11C &quot;\uBCC0\uD654&quot;\uB77C\uB294 \uD0A4\uC6CC\uB4DC\uC5D0 \uB300\uD574 \uC0DD\uAC01\uD588\uACE0, \uC9D1\uC73C\uB85C \uBCF5\uADC0 \uD6C4 \uC0DD\uAC01\uC774 \uD718\uBC1C\uB418\uC9C0 \uC54A\uB3C4\uB85D \uBCC0\uD654\uB77C\uB294 \uD0A4\uC6CC\uB4DC\uC5D0 \uB300\uD574 \uAE00\uC744 \uC4F0\uACE0 \uC788\uB2E4.</p><div class="custom-container tip"><p class="custom-container-title">\uBCC0\uD654</p><p>\uC774 \uAE00\uC5D0\uC11C \u2018\uBCC0\uD654\u2019\uB294 \uB204\uAD70\uAC00\uAC00 \uC790\uBC1C\uC801\uC73C\uB85C \uB530\uB77C \uD558\uAC8C \uB418\uB294 \uC0C1\uD0DC\uB97C \uB73B\uD55C\uB2E4. \uB098\uB294 \uADF8\uAC83\uC774 \uAC00\uC2DC\uD654\uB41C \uD589\uB3D9\uC5D0\uC11C \uC2DC\uC791\uB41C\uB2E4\uACE0 \uBBFF\uB294\uB2E4.</p></div><h2 id="f1-the-movie" tabindex="-1"><a class="header-anchor" href="#f1-the-movie" aria-hidden="true">#</a> F1 The Movie</h2><p>\uCD5C\uADFC\uC5D0 <strong>F1 \uB354 \uBB34\uBE44</strong> \uB77C\uB294 \uC601\uD654\uB97C \uBD24\uB2E4. \uC601\uD654\uC758 \uBAA8\uB4E0 \uC7A5\uBA74\uC774 \uD558\uB098\uD558\uB098 \uB2E4 \uC778\uC0C1 \uAE4A\uC5C8\uB294\uB370, \uD2B9\uD788 \uAE30\uC5B5\uC5D0 \uB0A8\uB294 \uC7A5\uBA74\uC744 \uAF3D\uC790\uBA74..</p><img width="300" alt="Image" src="https://github.com/user-attachments/assets/b193cf26-e6eb-49ff-8ef1-13170563b472"><p>\uC18C\uB2C8\uC640 \uD568\uAED8 \uC870\uAE45\uD558\uB294 \uC0AC\uB78C\uB4E4\uC774 \uC2DC\uAC04\uC774 \uD750\uB97C\uC218\uB85D \uB298\uC5B4\uB098\uB294 \uBAA8\uC2B5\uC774\uC5C8\uB2E4. \uD300 \uD569\uB958 \uCD08\uBC18\uC5D0 \uC18C\uB2C8\uB294 \uD63C\uC790\uC11C \uC870\uAE45\uC744 \uD588\uB2E4. \uD06C\uACE0 \uC791\uC740 \uC0AC\uAC74\uB4E4\uC744 \uACAA\uACE0, \uC810\uC810 \uC18C\uB2C8\uC5D0 \uB300\uD55C \uC2E0\uB8B0\uAC00 \uC0DD\uAE30\uBA74\uC11C \uC810\uC810 \uC18C\uB2C8\uC640 \uD568\uAED8 \uB6F0\uC5B4\uB2E4\uB2C8\uB294 \uC0AC\uB78C\uB4E4\uC774 \uC0DD\uAE34\uB2E4. \uC601\uD654 \uB9C9\uBC14\uC9C0\uC5D0\uB294 \uC18C\uB2C8\uAC00 \uC5C6\uC744 \uB54C \uB2E4 \uAC19\uC774 \uC870\uAE45\uD558\uB294 \uBAA8\uC2B5\uC744 \uBCF4\uBA74\uC11C &quot;\uADF8\uB798 \uD55C \uBC88 \uC2F8\uC6CC\uBCF4\uC790!&quot; \uB77C\uB294 \uB290\uB08C\uC744 \uBC1B\uC558\uB2E4.</p><p>\uC5B4\uB5BB\uAC8C \uC0AC\uB78C\uB4E4\uC740 \uC18C\uB2C8\uC640 \uB6F8 \uC0DD\uAC01\uC744 \uD558\uAC8C \uB418\uC5C8\uC744\uAE4C? \uC18C\uB2C8\uB294 \uD55C \uBC88\uB3C4 &quot;\uAC19\uC774 \uB6F0\uC790&quot; \uB77C\uACE0 \uC774\uC57C\uAE30\uD558\uB294 \uC7A5\uBA74\uC774 \uC5C6\uC5C8\uB2E4. <strong>\uC5B4\uB5A4 \uC0DD\uAC01\uC744 \uD558\uB294 \uC0AC\uB78C\uC778\uC9C0 \uD589\uB3D9\uC73C\uB85C \uBCF4\uC5EC\uC92C\uB2E4.</strong></p><p>\uADF8\uB9AC\uACE0 \uB204\uAD70\uAC00\uB294 \uADF8 \uBAA8\uC2B5\uC5D0 \uB300\uD574 &#39;\uC88B\uB2E4\uB294 \uB290\uB08C&#39;\uC744 \uBC1B\uC558\uB2E4\uACE0 \uC0DD\uAC01\uD55C\uB2E4. &#39;\uC18C\uB2C8\uAC00 \uC633\uB2E4\uACE0 \uC0DD\uAC01\uD574&#39; \uAE4C\uC9C4 \uC544\uB2C8\uC5B4\uB3C4, \uC18C\uB2C8\uC758 \uBAA8\uC2B5\uC744 \uBCF4\uACE0 \uC778\uC0AC\uC774\uD2B8\uB97C \uC5BB\uC5C8\uC744 \uAC83\uC774\uB2E4.</p><p>\uC18C\uB2C8\uB294 <strong>\uB9D0\uC774 \uC544\uB2CC \uD589\uB3D9\uC73C\uB85C \uD300\uC5D0\uAC8C \uC790\uC2E0\uC758 \uC0DD\uAC01\uACFC \uC790\uC2E0\uC758 \uAC10\uC815\uC744 \uC804\uB2EC</strong>\uD588\uB2E4\uACE0 \uC0DD\uAC01\uD55C\uB2E4. \uADF8\uB807\uAC8C \uD300\uC6D0 \uD55C \uBA85 \uD55C \uBA85\uC758 \uC2E0\uB8B0\uB97C \uC5BB\uC5C8\uACE0, \uC18C\uB2C8\uC758 \uC0DD\uAC01\uC744 \uAD81\uAE08\uD574\uD558\uACE0 \uC874\uC911\uD588\uC73C\uBA70 \uC790\uC5F0\uC2A4\uB7FD\uAC8C \uC18C\uB2C8\uC5D0\uAC8C \uC774\uBAA9\uC774 \uC9D1\uC911\uB418\uC5C8\uB2E4.</p><p>\uC774\uB807\uAC8C \uB204\uAD70\uAC00\uAC00 \uB098\uC5D0 \uB300\uD574 \uC9D1\uC911\uD558\uB3C4\uB85D, \uC8FC\uBAA9\uD558\uB3C4\uB85D \uB9CC\uB4E4 \uC218 \uC788\uB294 \uC0C1\uD0DC\uB97C \uB9CC\uB4E4 \uC218 \uC788\uB2E4\uBA74 \uADF8\uB54C\uBD80\uD130 \uD06C\uACE0 \uC791\uC740 \uBCC0\uD654\uB97C \uB9CC\uB4E4\uC5B4\uAC08 \uC218 \uC788\uB294 \uAC8C \uC544\uB2D0\uAE4C?</p><h2 id="n8n" tabindex="-1"><a class="header-anchor" href="#n8n" aria-hidden="true">#</a> n8n</h2><p>\uC791\uB144 \uB9D0\uC5D0 n8n \uC774\uB77C\uB294 \uB3C4\uAD6C\uC5D0 \uB300\uD574 \uD300\uC7A5\uB2D8\uAED8\uC11C \uAC00\uB054\uC529 \uC5B8\uAE09\uD588\uB2E4. &quot;\uC774\uB7F0\uAC78\uB85C \uC6B0\uB9AC\uB3C4 \uC790\uB3D9\uD654\uB97C \uC2DC\uB3C4\uD574 \uBCF4\uBA74 \uC88B\uACA0\uC5B4\uC694&quot; \uB77C\uB294 \uBA54\uC2DC\uC9C0\uB97C \uACC4\uC18D \uB358\uC9C4 \uAC83. \uC77C\uB2E8 \uB098\uB294 \uD300\uC7A5\uB2D8\uC744 \uC874\uACBD\uD558\uACE0 \uC2E0\uB8B0\uD55C\uB2E4. \uBAA8\uB4E0 \uB9D0\uC744 \uC8FC\uC6CC\uB2F4\uC744 \uC218\uB294 \uC5C6\uC9C0\uB9CC, \uD300\uC7A5\uB2D8\uC758 \uC0DD\uAC01\uC744 \uCAD3\uC544\uAC00 \uBCF4\uB824\uACE0 \uC560\uC4F0\uB294 \uD3B8\uC774\uB2E4. \uADF8\uB798\uC11C n8n \uC774\uB77C\uB294 \uAC83\uC744 \uC0AC\uC6A9\uD560 \uC218 \uC788\uB294 \uD658\uACBD\uC774 \uAC16\uCDB0\uC84C\uC744 \uB54C, <strong>\uC77C\uB2E8 n8n \uC73C\uB85C \uADF8 \uB54C\uC758 \uB0B4\uAC00 \uACAA\uACE0 \uC788\uB294 \uBB38\uC81C\uB97C \uD574\uACB0\uD560 \uC218 \uC788\uB294 \uAC04\uB2E8\uD55C \uC6CC\uD06C\uD50C\uB85C\uC6B0\uB97C \uB9CC\uB4E4\uC5C8\uB2E4.</strong></p><p><img src="'+t+'" alt="n8n"></p><p>\uD300\uC6D0\uB4E4\uC774 \uB208\uC73C\uB85C n8n\uC758 \uC2E4\uCCB4\uB97C \uBCF4\uAC8C \uB418\uC5C8\uC744 \uB54C, \uC815\uC2E0 \uCC28\uB824 \uBCF4\uB2C8 \uC218\uC2ED \uAC1C\uC758 n8n \uC6CC\uD06C\uD50C\uB85C\uC6B0\uAC00 \uC0DD\uACBC\uB2E4.</p><p>n8n\uC5D0 \uB300\uD55C \uB0B4\uC6A9\uC744 \uD55C \uB2EC\uC5D0 \uD55C \uBC88 \uC9C4\uD589\uD558\uB294 \uC0C1\uC704 \uC870\uC9C1\uC758 \uC62C\uD578\uC988 \uBBF8\uD305\uC5D0\uC11C\uB3C4 \uC774\uC57C\uAE30\uB97C \uAEBC\uB0C8\uB2E4. \uC0AC\uC2E4 \uC790\uC8FC \uAEBC\uB0C8\uB2E4.</p><p>n8n\uC73C\uB85C \uACC4\uC18D \uBB34\uC5B8\uAC00\uB97C \uB9CC\uB4E4\uC5B4\uB0C8\uB2E4. \uAC04\uB2E8\uD55C \uC6CC\uD06C\uD50C\uB85C\uC6B0\uBD80\uD130 \uC2DC\uC791\uD574\uC11C AI\uC640 \uC5F0\uACC4\uD560 \uC218 \uC788\uB294 API\uB3C4 \uB9CC\uB4E4\uACE0, MCP\uB3C4 \uB9CC\uB4E4\uACE0, RAG\uB3C4 \uB9CC\uB4E4\uACE0. \uADF8\uB9AC\uACE0 \uC774 \uC774\uC57C\uAE30\uB97C \uAEBC\uB0BC \uC218 \uC788\uB294 \uAE30\uD68C\uAC00 \uC788\uB294 \uAE30\uD68C\uAC00 \uC788\uC744 \uB54C\uB9C8\uB2E4 \uC18C\uAC1C\uB97C \uD588\uB2E4.</p><p>\uC870\uC9C1 \uCC28\uC6D0\uC5D0\uC11C &quot;\uC694\uC998 \uC54C\uACE0 \uC2F6\uC740 AI \uB3C4\uAD6C&quot;\uC5D0 \uB300\uD574 \uC124\uBB38\uC744 \uD588\uC744 \uB54C n8n\uC5D0 \uB300\uD55C \uC774\uC57C\uAE30\uAC00 \uB2E4\uC218 \uC788\uC5C8\uACE0,</p><img src="'+p+'" alt="AI \uB3C4\uAD6C \uC124\uBB38" style="max-width:100%;width:400px;"><p>\uD604\uC7AC \uC870\uC9C1\uC5D0\uC11C n8n\uC5D0 \uB300\uD574 \uACC4\uC18D \uC18C\uAC1C\uD558\uACE0 \uB5A0\uB4E4\uBA74\uC11C &quot;\uC774\uAC70 \uC88B\uC544\uC694!&quot;\uB77C\uACE0 \uC774\uC57C\uAE30 \uD574\uC628 \uC0AC\uB78C\uC774 \uB098\uC5EC\uC11C \uACB0\uAD6D \uB0B4\uAC00 n8n\uC5D0 \uB300\uD55C \uC18C\uAC1C\uC640 \uD65C\uC6A9\uBC95\uC5D0 \uB300\uD574 \uBC1C\uD45C\uB97C \uD558\uAC8C \uB418\uC5C8\uB2E4.</p><img src="'+r+'" alt="\uBA54\uC138\uC9C0" style="max-width:100%;width:700px;"><p>\uC815\uC2E0\uCC28\uB824\uBCF4\uB2C8 \uB0B4\uC77C\uBAA8\uB808 400\uBA85 \uC815\uB3C4 \uB418\uB294 \uC870\uC9C1\uC6D0 \uC55E\uC5D0\uC11C n8n\uC744 \uC8FC\uC81C\uB85C \uBC1C\uD45C\uB97C \uD558\uAC8C \uB418\uC5C8\uB2E4. <s>\uC5B4\uB5A1\uD558\uC9C0... \uC544\uC9C1 \uBC1C\uD45C \uC790\uB8CC\uB3C4 \uBABB \uB9CC\uB4E4\uC5C8\uB2E4</s></p><p><strong>\uC774\uB7EC\uD55C \uD750\uB984\uC740 \uD589\uB3D9\uC744 \uD1B5\uD574 &quot;\uC88B\uC544 \uBCF4\uC774\uB294 \uC0C1\uD0DC&quot;\uB97C \uBCF4\uC5EC\uC8FC\uB294 \uAC83\uC5D0\uC11C \uC2DC\uC791\uC774 \uB41C \uAC8C \uC544\uB2D0\uAE4C?</strong></p><h2 id="\u1107\u1173\u11AF\u1105\u1169\u1100\u1173-\u1109\u1173\u1110\u1165\u1103\u1175" tabindex="-1"><a class="header-anchor" href="#\u1107\u1173\u11AF\u1105\u1169\u1100\u1173-\u1109\u1173\u1110\u1165\u1103\u1175" aria-hidden="true">#</a> \uBE14\uB85C\uADF8 \uC2A4\uD130\uB514</h2><p><span class="link-preview"><a href="https://github.com/zum-study/zum-blog-study" target="_blank" rel="noopener noreferrer"><span class="link-preview-image"><img src="https://opengraph.githubassets.com/461cee4a78465013d89e3963e5aa0626091a2e82e975c913796a0f70f0077f85/zum-study/zum-blog-study" alt="GitHub - zum-study/zum-blog-study: \uC90C\uC778\uD130\uB137 \uC0AC\uB0B4 \uBE14\uB85C\uADF8 \uC2A4\uD130\uB514"></span><span class="link-preview-content"><span class="link-preview-title">GitHub - zum-study/zum-blog-study: \uC90C\uC778\uD130\uB137 \uC0AC\uB0B4 \uBE14\uB85C\uADF8 \uC2A4\uD130\uB514</span><span class="link-preview-description">\uC90C\uC778\uD130\uB137 \uC0AC\uB0B4 \uBE14\uB85C\uADF8 \uC2A4\uD130\uB514. Contribute to zum-study/zum-blog-study development by creating an account on GitHub.</span><span class="link-preview-url">GitHub</span></span></a></span></p><p>\uC774\uC804 \uD68C\uC0AC\uC5D0\uC11C \uBE14\uB85C\uADF8 \uC2A4\uD130\uB514\uB97C 2\uB2EC \uC815\uB3C4 \uC9C4\uD589\uD588\uC5C8\uB2E4. \uB098\uB97C \uD3EC\uD568\uD558\uC5EC 4\uBA85\uC774 \uCC38\uC5EC\uD588\uACE0, 2\uC8FC \uAC04\uACA9\uC73C\uB85C \uAE00 \uD55C \uD3B8\uC744 \uC791\uC131\uD55C \uB2E4\uC74C \uAC1C\uBC1C \uC870\uC9C1\uC5D0 \uACF5\uC720\uD588\uC5C8\uB2E4.</p><p><span class="link-preview"><a href="https://github.com/zum-study/zum-blog-study/tree/main/%ED%99%A9%EC%A4%80%EC%9D%BC" target="_blank" rel="noopener noreferrer"><span class="link-preview-image"><img src="https://opengraph.githubassets.com/461cee4a78465013d89e3963e5aa0626091a2e82e975c913796a0f70f0077f85/zum-study/zum-blog-study" alt="zum-blog-study/\uD669\uC900\uC77C at main \xB7 zum-study/zum-blog-study"></span><span class="link-preview-content"><span class="link-preview-title">zum-blog-study/\uD669\uC900\uC77C at main \xB7 zum-study/zum-blog-study</span><span class="link-preview-description">\uC90C\uC778\uD130\uB137 \uC0AC\uB0B4 \uBE14\uB85C\uADF8 \uC2A4\uD130\uB514. Contribute to zum-study/zum-blog-study development by creating an account on GitHub.</span><span class="link-preview-url">GitHub</span></span></a></span></p><p>\uADF8\uB807\uAC8C \uB450 \uB2EC \uC815\uB3C4 \uC2A4\uD130\uB514\uB97C \uC9C4\uD589\uD55C \uB2E4\uC74C\uC5D0 \uC870\uAE08 \uC26C\uC5C8\uB2E4\uAC00 \uB2E4\uC2DC \uC0C8\uB85C \uC2A4\uD130\uB514\uB97C \uBAA8\uC9D1\uD588\uB294\uB370 \uAC11\uC790\uAE30 10\uBA85\uC774 \uB118\uB294 \uC0AC\uB78C\uC774 \uC2E0\uCCAD\uD558\uC5EC \uB2F9\uD669\uD588\uC5C8\uB2E4. \uC0AC\uB78C\uC774 \uAC11\uC790\uAE30 \uD655 \uB9CE\uC544\uC838\uC11C \uBA3C\uC800 \uC5B4\uB5BB\uAC8C \uC6B4\uC601\uD558\uB294 \uAC8C \uC88B\uC744\uC9C0, \uC2DC\uAC04\uC744 \uC5B4\uB5BB\uAC8C \uC368\uC57C \uC88B\uC744\uC9C0 \uB4F1\uC5D0 \uB300\uD574 \uACE0\uB824\uB97C \uBABB\uD588\uB2EC\uAE4C..</p><p>\uC5EC\uD2BC \uC778\uC6D0\uC774 \uB9CE\uC544\uC9C4 \uC774\uD6C4\uC5D0 \uC2A4\uD130\uB514\uB294 \uD750\uC9C0\uBD80\uC9C0 \uB418\uC5C8\uB358\uAC78\uB85C \uAE30\uC5B5\uD55C\uB2E4.</p><p>\uC5EC\uAE30\uC11C \uB0B4\uAC00 \uC911\uC694\uD558\uAC8C \uC0DD\uAC01\uD558\uB294 \uBD80\uBD84\uC740 &quot;\uC2A4\uD130\uB514 \uD574\uC694!&quot;\uB97C \uAC15\uC870\uD588\uB2E4\uAE30\uBCF4\uB2E8.. <strong>\uB0B4\uAC00 \uC791\uC131\uD55C \uAE00\uC744 \uBCF4\uC5EC\uC8FC\uBA74\uC11C &quot;\uB098\uB3C4 \uC368 \uBCF4\uACE0 \uC2F6\uB2E4.&quot; \uB77C\uB294 \uB9C8\uC74C\uC774 \uB4E4\uB3C4\uB85D \uB9CC\uB4E4\uC5C8\uB2E4\uACE0 \uC0DD\uAC01\uD55C\uB2E4.</strong></p><p>\uADF8\uB9AC\uACE0 \uB2E4\uC591\uD55C \uAD50\uC721\uACFC\uC815\uC5D0 \uBA58\uD1A0\uB85C \uCC38\uC5EC\uD560 \uB54C, \uAD50\uC721\uC744 \uC6B4\uC601\uD558\uB294 \uBD84\uB4E4\uAED8\uC11C &quot;\uAE00\uC4F0\uB294 \uBB38\uD654\uB97C \uC5B4\uB5BB\uAC8C \uB9CC\uB4E4\uC5B4\uAC00\uBA74 \uC88B\uC744\uAE4C\uC694?&quot; \uB4F1\uC758 \uC9C8\uBB38\uC744 \uB9CE\uC774 \uD574\uC8FC\uC2E0\uB2E4. \uAE00\uC4F0\uAE30\uAC00 \uC88B\uC740\uAC78 \uBAA8\uB974\uB294 \uC0AC\uB78C\uC740 \uC5C6\uB2E4. \uADF8\uCE58\uB9CC \uAE00\uC4F0\uAE30\uB294 \uAE00\uC744 \uC4F0\uACE0 \uC2F6\uC5B4\uC57C \uB354 \uC790\uC8FC \uB354 \uC798 \uC4F8 \uC218 \uC788\uB2E4\uACE0 \uC0DD\uAC01\uD55C\uB2E4.</p><p><strong>\uC4F0\uACE0 \uC2F6\uB3C4\uB85D \uB9CC\uB4E4\uC5B4\uC57C \uD55C\uB2E4. \uD558\uACE0 \uC2F6\uB3C4\uB85D \uB9CC\uB4E4\uC5B4\uC57C \uD55C\uB2E4.</strong></p><p>\uADF8\uB798\uC11C \uBB34\uC5B8\uAC00 \uBCC0\uD654\uB97C \uC77C\uC73C\uD0A4\uACE0 \uC2F6\uC744 \uB54C\uB294 &quot;\uB9C8\uC74C&quot;\uC744 \uC790\uADF9\uD574\uC57C \uD55C\uB2E4\uACE0 \uC0DD\uAC01\uD55C\uB2E4. \uAD6C\uC131\uC6D0 \uC911 \uB204\uAD70\uAC00\uAC00 \uC798 \uC791\uC131\uD55C \uAE00\uC744 \uBCF4\uC5EC\uC8FC\uB294 \uAC83. \uC131\uC7A5\uD558\uB294 \uBAA8\uC2B5\uC744 \uBCF4\uC5EC\uC8FC\uB294 \uAC83. \uADF8\uAC8C \uC0AC\uB78C\uB4E4\uC5D0\uAC8C \uC778\uC0AC\uC774\uD2B8\uAC00 \uC788\uC5B4\uC57C \uD55C\uB2E4.</p><h2 id="ai-\u110F\u1169\u1103\u1173\u1105\u1175\u1107\u1172" tabindex="-1"><a class="header-anchor" href="#ai-\u110F\u1169\u1103\u1173\u1105\u1175\u1107\u1172" aria-hidden="true">#</a> AI \uCF54\uB4DC\uB9AC\uBDF0</h2><p>\uCD5C\uADFC\uC5D0 \uD55C \uAD50\uC721\uACFC\uC815\uC5D0 AI \uCF54\uB4DC\uB9AC\uBDF0\uB97C \uC801\uC6A9\uD588\uB2E4.</p><p><img src="'+o+'" alt="n8n \uCF54\uB4DC\uB9AC\uBDF0"></p><p>\uBB34\uC5B8\uAC00 \uC874\uC7AC\uD558\uB294 \uD50C\uB7AB\uD3FC\uC744 \uC0AC\uC6A9\uD55C\uAC8C \uC544\uB2C8\uB77C, \uACFC\uC81C\uC758 \uD2B9\uC131\uACFC \uB098\uC758 \uC0DD\uAC01\uC744 \uCD5C\uB300\uD55C \uB2F4\uC544\uBCF4\uB824\uACE0 \uD588\uB2E4. \uC0AC\uC2E4 \uCF54\uB4DC\uB9AC\uBDF0\uB97C \uD1B5\uD574 \uD070 \uD6A8\uACFC\uB97C \uBCF4\uB294 \uAC83 \uBCF4\uB2E4 \uC774 \uC77C\uC744 \uD558\uACE0 \uC2F6\uC5C8\uB358 \uAC83 \uAC19\uB2E4. \uB9CC\uB4E4\uBA74\uC11C \uC7AC\uBC0C\uC5C8\uB2E4. \uC774\uC804\uC5D0 &quot;AI \uCF54\uB4DC\uB9AC\uBDF0\uAC00 \uC788\uC73C\uBA74 \uC88B\uACA0\uB2E4&quot;\uACE0 \uB0B4\uAC00 \uC218\uAC15\uC0DD\uB4E4\uC5D0\uAC8C \uC774\uC57C\uAE30\uD588\uB2E4. \uB204\uAD70\uAC00\uAC00 \uB9CC\uB4E4\uC5B4\uC8FC\uAE38 \uD558\uB294 \uB9C8\uC74C\uC73C\uB85C..</p><p>\uC774\uB7F0 \uC2DC\uC2A4\uD15C\uC774 \uC788\uC73C\uBA74 \uC88B\uB2E4\uB294 \uAC83\uC744 \uB204\uAD6C\uB098 \uC54C\uACE0 \uC788\uB2E4. \uADF8\uCE58\uB9CC \uC88B\uB2E4\uB294 \uAC83\uC744 \uC54C\uC544\uB3C4 \uD589\uB3D9\uD558\uB294 \uC0AC\uB78C\uC740 \uB4DC\uBB3C\uB2E4.</p><p>\uADF8\uB7F0\uB370 AI \uCF54\uB4DC\uB9AC\uBDF0 \uC2DC\uC2A4\uD15C\uC744 \uB9CC\uB4E4\uC5B4 \uB1A8\uB354\uB2C8 &quot;\uADF8\uAC70 \uC5B4\uB5BB\uAC8C \uB9CC\uB4E4\uC5B4\uC694? \uC800\uB3C4 \uB9CC\uB4E4\uACE0 \uC2F6\uC5B4\uC694!&quot; \uB4F1\uC758 \uC774\uC57C\uAE30\uB97C \uD558\uBA74\uC11C \uBA54\uC138\uC9C0\uB97C \uBCF4\uB0B4\uACE0 \uC9C1\uC811 \uC790\uAE30\uB9CC\uC758 AI \uCF54\uB4DC\uB9AC\uBDF0 \uC2DC\uC2A4\uD15C\uC744 \uB9CC\uB4DC\uB294 \uBAA8\uC2B5\uC744 \uBAA9\uACA9\uD588\uB2E4. \uC2E4\uCCB4\uAC00 \uB208\uC55E\uC5D0 \uC788\uC5B4\uC11C \uADF8\uB7F0\uAC78\uAE4C?</p><img alt="n8n \uCF54\uB4DC\uB9AC\uBDF0 2" src="'+i+'" style="max-height:300px;"><img alt="n8n \uCF54\uB4DC\uB9AC\uBDF0 3" src="'+u+'" style="max-height:300px;"><p>\uC5B4\uCA0C\uB4E0 \uC774\uBC88\uC5D0\uB3C4 \uAC19\uC740 \uD328\uD134\uC774\uC5C8\uB2E4. (\uD589\uB3D9) \uBA3C\uC800 \uB9CC\uB4E4\uACE0 \u2192 (\uAC00\uC2DC\uD654) \uBCF4\uC774\uAC8C \uD558\uACE0 \u2192 (\uACF5\uBA85) \uB9E5\uB77D\uC744 \uACF5\uC720\uD558\uC790 \u2192 (\uD655\uC0B0) \uB530\uB77C \uD558\uB294 \uC0AC\uB78C\uC774 \uC0DD\uACBC\uB2E4.</p><h2 id="\u110C\u1165\u11BC\u1105\u1175" tabindex="-1"><a class="header-anchor" href="#\u110C\u1165\u11BC\u1105\u1175" aria-hidden="true">#</a> \uC815\uB9AC</h2><p>\uB0B4 \uC8FC\uBCC0 \uD658\uACBD\uC744 \uBCC0\uD654\uC2DC\uD0A4\uB294 \uAC83\uC740 \uBB34\uCC99 \uC5B4\uB835\uB2E4. \uB0B4\uAC00 \uC633\uB2E4\uACE0 \uBBFF\uB294 \uAC83\uC744 \uC774\uC57C\uAE30 \uD558\uACE0, \uC124\uB4DD\uD558\uACE0, \uAC19\uC774 \uC6C0\uC9C1\uC774\uAC8C \uB9CC\uB4E4\uACE0. \uC0C1\uB2F9\uD788 \uD53C\uB85C\uD55C \uACFC\uC815\uC774\uB2E4.</p><p>\uB098\uB294 \uB17C\uB9AC\uB3C4 \uC911\uC694\uD558\uC9C0\uB9CC \uACB0\uAD6D <strong>\uC0AC\uB78C\uC774 \uC6C0\uC9C1\uC774\uAC8C \uB418\uB294 \uC21C\uAC04\uC740 \uB0B4\uBA74\uC5D0 \uC788\uB294 \uC695\uAD6C\uB97C \uC790\uADF9\uD588\uC744 \uB54C</strong>\uB77C\uACE0 \uC0DD\uAC01\uD55C\uB2E4. \uC695\uAD6C\uC758 \uD615\uD0DC\uB294 \uC0AC\uB78C\uB9C8\uB2E4 \uB2E4\uB974\uACE0, \uB204\uAD70\uAC00\uAC00 \uB0B4\uAC00 \uAC00\uC9C4 \uC695\uAD6C\uB97C \uC790\uADF9\uD560 \uB54C, \uADF8 \uB54C \uAFC8\uD2C0\uAC70\uB9AC\uBA74\uC11C \uC6C0\uC9C1\uC774\uB294\uAC8C \uC544\uB2D0\uAE4C?</p><p>\uB204\uAD70\uAC00\uAC00 \uB098\uB97C \uC2E0\uB8B0\uD558\uB3C4\uB85D \uB9CC\uB4E4\uC5B4\uC57C \uD558\uACE0 \uB0B4 \uC774\uC57C\uAE30\uC5D0 \uADC0\uB97C \uAE30\uC6B8\uC774\uB3C4\uB85D \uB9CC\uB4E4\uC5B4\uC57C \uD558\uB294 \uB370 \uADF8\uB7F0 \uACFC\uC815\uC740 \uC5B8\uC81C\uB098 \uC5B4\uB835\uB2E4. \uB54C\uB85C\uB294 \uB0B4\uAC00 \uD558\uACE0 \uC2F6\uC740 \uC77C\uC744 \uD558\uB294 \uAC8C \uB2F5\uC774 \uB420 \uC218\uB3C4 \uC788\uACE0 \uB54C\uB85C\uB294 \uC8FC\uBCC0\uC758 \uBB38\uC81C\uB97C \uD574\uACB0\uD558\uB294 \uAC8C \uB2F5\uC774 \uB420 \uC218\uB3C4 \uC788\uB2E4.</p><p>\uADF8\uAC74 \uACB0\uAD6D \uB0B4\uAC00 \uC5B4\uB5A4 \uC0AC\uB78C\uC774\uB0D0\uC5D0 \uB530\uB77C \uB2E4\uB974\uB2E4\uACE0 \uC0DD\uAC01\uD55C\uB2E4. \uB354 \uC815\uD655\uD788\uB294, \uC5B4\uB5A4 \uC5D0\uB108\uC9C0\uB97C \uAC00\uC9C0\uACE0 \uC788\uB294 \uC0AC\uB78C\uC774\uB0D0\uC5D0 \uB530\uB77C \uC804\uB7B5\uC744 \uC138\uC6CC\uBA74 \uB418\uC9C0 \uC54A\uC744\uAE4C?</p><p><strong>\uC5B4\uCA0C\uB4E0 \uBCC0\uD654\uB294 \uD589\uB3D9\uC758 \uAC00\uC2DC\uD654\uC5D0\uC11C \uC2DC\uC791\uB41C\uB2E4.</strong> \uC77C\uB2E8 \uD589\uB3D9\uD558\uC790.</p>',49),l=[d];function h(m,g){return a(),e("div",null,l)}var _=s(c,[["render",h],["__file","index.html.vue"]]);export{_ as default};
